mapper.update:
update userinfo set id=#{id}, password=#{password}, nickname=#{nickname},
sex=#{sex}, phone=#{phone}, adminCheck=#{adminCheck}, email=#{email}
where idx=#{idx}

mapper.select:
select b.boardIdx, b.boardCode, b.title, b.writeDate, b.readCount
from board b, userinfo u
where u.nickname = b.writer and u.id = #{id}
